/**
 * Get error definition by code
 */
export function getError(code: string): ErrorDefinition | undefined {
  return ERRORS[code];
}

/**
 * Check if error is retriable
 */
export function isRetriable(code: string): boolean {
  return ERRORS[code]?.retriable ?? false;
}

